<script>
import items from '../data.json';
import TimeAgo from 'javascript-time-ago'
import en from 'javascript-time-ago/locale/en.json'
import VueSlider from 'vue-slider-component';
import graph from './graph'


TimeAgo.addDefaultLocale(en)
const timeAgo = new TimeAgo('en-US')
const PAGE_SIZE = 50;

export default {
    name: 'IndexPage',
    components: {VueSlider, graph},
    data() {
        return {
            selectedItem: null,
            items: items,
            search: "",
            itemsLimit: PAGE_SIZE,
            filters: {servers: [], dates: [], usersMap: {}, users: ["single", "multiple"]},
            container: this.$refs.cont,
            datesRangeValues: [],
            allDates: [],
            serversFilter: [],
        }
    },
    methods: {
        formatDate(date) {
            date = new Date(date);
            return timeAgo.format(date)
        },
        setSelectedItem(item) {
            this.selectedItem = item;
        },
        showMore() {
            this.itemsLimit += PAGE_SIZE;
        },
        resetLimit() {
            this.itemsLimit = PAGE_SIZE;
        },
        formatServer(server) {
            return `*.${server.split('.').slice(-2).join(".")}`
        },
        initFilters() {
            let items = this.items
            let servers = new Set();
            let usersAmount = {};
            let datesRange = new Set();
            items.forEach((item) => {
                if (item.server) {
                    servers.add(item.server)
                }
                if (!Object.keys(usersAmount).includes(item.username)) {
                    if (this.items.filter((filterItem) => filterItem.username === item.username).length > 1) {
                        usersAmount[item.username] = "multiple"
                    } else {
                        usersAmount[item.username] = "single"
                    }
                }
                datesRange.add(item.date)
            })
            this.search = ""
            this.filters.users = ["single", "multiple"]
            this.serversFilter = Array.from(servers)
            this.filters.servers = Array.from(servers)
            this.filters.usersMap = usersAmount
            let sortedDates = Array.from(datesRange).sort((a, b) => new Date(a) - new Date(b))
            this.allDates = sortedDates
            this.filters.dates = ["2022-06-29", sortedDates[sortedDates.length - 1]]
        }
    },
    computed: {
        selectAllServers: {
            get(){
                return this.filters.servers.length === this.serversFilter.length;
            },
            set(value){
                if (this.selectAllServers) {
                    this.filters.servers = [];
                } else {
                    this.filters.servers = Array.from(this.serversFilter)
                }
            }
        },
        maliciousPackagesCount() {
            return this.items.length
        },
        userAccountsCount() {
            let users = new Set();
            this.items.forEach(item => users.add(item.username));
            return users.size;
        },
        exfiltrationEndpointsCount() {
            let servers = new Set();
            this.items.filter(item => item.server).forEach(item => servers.add(item.server));
            return servers.size;
        },
        filteredItems() {
            let items = this.items;
            let search = this.search;

            if (search) {
                items = items.filter(x => x.name.includes(search))
            }
            return items;
        }
    },
    mounted() {
        this.initFilters()
    }
}
</script>

