.package {
    width: 360px;
    min-height: 80px;
    background: #fff;
    border-radius: 2px;
    display: flex;
    flex-direction: row;
    overflow: hidden;
    padding-top: $padding*2;
    padding-right: $padding*2;
    padding-bottom: $padding*2;
    user-select: none;
    cursor: pointer;
    color: #856969;
    transition: 0.10s ease-out;
    $class-name: &;
    opacity: 0.6;

    @include responsive($breakpoint-tablet-landscape) {
        width: 340px;
    }

    @include responsive($breakpoint-mobile) {
        width: 100%;
    }

    @keyframes pulse {
        0% {
        }
        30% {
        }
        40% {
            transform: perspective(200px) rotateX(20deg) scaleX(0.95) scaleY(0.95);
        }
        50% {

        }
    }

    &:hover {
        box-shadow: 0 0 0 6px #BC0000;
        opacity: 1;

    }

    &#{$class-name}--available {
        opacity: 1;
        box-shadow: 0 0 30px 10px #BC0000;

        &:hover {
            box-shadow: 0 0 30px 10px #BC0000, 0 0 0 6px #BC0000;
        }

        @include responsive($breakpoint-mobile) {
            box-shadow: 0 0 10px 5px #BC0000;
        }
    }


    &__icon {
        width: 70px;
        display: flex;
        flex-shrink: 0;
        justify-content: center;

    }

    &__info {
        overflow: hidden;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        width: 100%;

        &__top {
            display: flex;
            flex-direction: column;
        }

        &__bottom {
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            align-items: flex-end;
            min-height: 40px;

        }

        &__available {
            background: #BC0000;
            color: #fff;
            text-transform: uppercase;
            padding: calc(#{$padding} / 2) $padding;
            border-radius: 4px;
            font-weight: 700;
            font-size: 12px;
            white-space: nowrap;


            @include responsive($breakpoint-mobile) {
                font-size: 10px;
            }

            animation-name: pulse;
            animation-duration: 1.0s;
            animation-direction: alternate;
            animation-iteration-count: infinite;
            animation-play-state: running;
        }

        &__name {
            overflow: hidden;
            white-space: nowrap;
            text-overflow: ellipsis;
            font-size: 16px;
            font-weight: 700;
            color: #c30000;

            @include responsive($breakpoint-mobile-small) {
                max-width: 180px;
            }

        }

        &__version {
            color: #856969;
        }
    }
}

.show-more-button {
    border-radius: 4px;
    user-select: none;
    padding: $padding*1.5 $padding*2;
    cursor: pointer;
    font-weight: 700;
    transition: all 0.1s ease-in-out;
    color: #fff;
    text-decoration: underline;
    font-size: 16px;

    @include responsive($breakpoint-mobile) {
        font-size: 12px;
    }


    &:hover {
        background: #fff;
        color: #000;
        text-decoration: none;
    }

    &__wrapper {
        display: flex;
        flex-direction: row;
        justify-content: center;
    }

}

.vue-slider-disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

$slider-main-color: #fff;
$slider-accent-color: #c00c0c;

.vue-slider-rail {
    background-color: $slider-main-color;
    border-radius: 15px;
}

.vue-slider-process {
    background-color: $slider-main-color;
    border-radius: 15px;
}

.vue-slider-mark {
    z-index: 4;
}

.vue-slider-mark:first-child .vue-slider-mark-step, .vue-slider-mark:last-child .vue-slider-mark-step {
    display: none;
}

.vue-slider-mark-step {
    width: 40%;
    height: 100%;
    background-color: #ffe7e7;
}

.vue-slider-mark-label {
    font-size: 14px;
    white-space: nowrap;
}

.vue-slider-dot-handle {
    cursor: pointer;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background-color: $slider-accent-color;
    box-sizing: border-box;
    box-shadow: 0.5px 0.5px 2px 1px rgba(0, 0, 0, 0.32);
}

.vue-slider-dot-handle-focus {
    box-shadow: 0px 0px 1px 2px rgba(52, 152, 219, 0.36);
}

.vue-slider-dot-handle-disabled {
    cursor: not-allowed;
    background-color: #ccc;
}

.vue-slider-dot-tooltip-inner {
    font-size: 12px;
    white-space: nowrap;
    padding: 2px 5px;
    min-width: 16px;
    text-align: center;
    color: #fff;
    border-radius: 5px;
    background-color: $slider-accent-color;
    box-sizing: content-box;
}

.vue-slider-dot-tooltip-inner::after {
    content: "";
    position: absolute;
}

.vue-slider-dot-tooltip-inner-top::after {
    top: 100%;
    left: 50%;
    transform: translate(-50%, 0);
    height: 0;
    width: 0;
    border-color: transparent;
    border-style: solid;
    border-width: 5px;
    border-top-color: inherit;
}

.vue-slider-dot-tooltip-inner-bottom::after {
    bottom: 100%;
    left: 50%;
    transform: translate(-50%, 0);
    height: 0;
    width: 0;
    border-color: transparent;
    border-style: solid;
    border-width: 5px;
    border-bottom-color: inherit;
}

.vue-slider-dot-tooltip-inner-left::after {
    left: 100%;
    top: 50%;
    transform: translate(0, -50%);
    height: 0;
    width: 0;
    border-color: transparent;
    border-style: solid;
    border-width: 5px;
    border-left-color: inherit;
}

.vue-slider-dot-tooltip-inner-right::after {
    right: 100%;
    top: 50%;
    transform: translate(0, -50%);
    height: 0;
    width: 0;
    border-color: transparent;
    border-style: solid;
    border-width: 5px;
    border-right-color: inherit;
}

.vue-slider-dot-tooltip-wrapper {
    opacity: 0;
    transition: all 0.3s;
}

.vue-slider-dot-tooltip-wrapper-show {
    opacity: 1;
}

.vue-slider, .vue-slider-ltr {
    max-width: 176px;
}


</style>
